import React, { useState } from 'react';

function App() {
  const [token, setToken] = useState(null);
  const [tokenPayload, setTokenPayload] = useState(null);

  const handleSignIn = (url) => {
    window.location.href = url;
  };

  const getTokenFromBackend = (code) => {
    // Simulate fetching token from backend
    const fakeToken = {
      access_token: 'fake_access_token',
      token_type: 'Bearer',
      expires_in: 3600,
      scope: 'http://idmanagement.gov/ns/assurance/ial/1/aal/1',
      id_token: 'fake_id_token'
    };
    setToken(fakeToken.access_token);
    setTokenPayload(fakeToken);
  };

  const getTokenFromUrl = () => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');

    if (code) {
      getTokenFromBackend(code);
    }
  };

  React.useEffect(() => {
    getTokenFromUrl();
  }, []);

  return (
    <div className="App" style={{ textAlign: 'center', padding: '50px' }}>
      <h1>Sign In with ID.me</h1>

      <button
        onClick={() =>
          handleSignIn(
            'https://api.idmelabs.com/oauth/authorize?client_id=0f2ce521178825f83f986daa5ce0b2d3&redirect_uri=http://localhost:3020/authorization-code/callback&response_type=code&scope=http://idmanagement.gov/ns/assurance/ial/1/aal/1'
          )
        }
        style={{
          backgroundImage:
            'url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-YMPjOgvJ8IbY3KMptZylE9T_wjh1rhWa5Q&s)',
          backgroundSize: 'cover',
          width: '200px',
          height: '50px',
          border: 'none',
          cursor: 'pointer',
        }}
      ></button>

      <br /><br />

      <button
        onClick={() =>
          handleSignIn(
            'https://api.idmelabs.com/oauth/authorize?client_id=0f2ce521178825f83f986daa5ce0b2d3&redirect_uri=http://localhost:3020/authorization-code/callback&response_type=code&scope=http://idmanagement.gov/ns/assurance/ial/1/aal/2'
          )
        }
        style={{
          backgroundImage:
            'url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT-YMPjOgvJ8IbY3KMptZylE9T_wjh1rhWa5Q&s)',
          backgroundSize: 'cover',
          width: '200px',
          height: '50px',
          border: 'none',
          cursor: 'pointer',
        }}
      ></button>

      {token && (
        <div style={{ marginTop: '20px' }}>
          <h2>Token:</h2>
          <p>{token}</p>
          <h2>Token Payload:</h2>
          <pre>{JSON.stringify(tokenPayload, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
